CREATE OR ALTER VIEW VWITENSPEDIDO(
    CODIGO,
    CODIGOPEDIDO,
    CODIGOPRODUTO,
    QUANTIDADE,
    VALORUNITARIO,
    DETALHES,
    DATAHORACADASTRO,
    CODIGOPAGAMENTO,
    IMPRESSO,
    CODIGOPERSONALIZADO,
    NOME,
    ESTOQUECONTROLADO,
    ITEMCOZINHA,
    ISENTOTAXASERVICO,
    CODIGOCOZINHA,
    ITEMPORKG,
    NUMERO,
    CODIGOPEDIDOORIGEM,
    DESCRICAOCOZINHA,
    IMPRESSORA,
    PORTA,
    IP,
    NOMECOLABORADOR,
    SUBTOTAL,
    OBSERVACAO)
AS
SELECT
              ItensPedido.Codigo,
              ItensPedido.CodigoPedido,
              ItensPedido.CodigoProduto,
              ItensPedido.Quantidade,
              ItensPedido.ValorUnitario,
              ItensPedido.Detalhes,    
              ItensPedido.DataHoraCadastro,   
			  ItensPedido.CodigoPagamento,
              CASE WHEN (Produtos.ItemCozinha = 'S' OR Pedidos.CodigoPedidoOrigem = 3) THEN ItensPedido.Impresso ELSE NULL END as IMPRESSO,       
              Produtos.CodigoPersonalizado,
              Produtos.Nome,
              Produtos.EstoqueControlado,   
              Produtos.ItemCozinha,   
              Produtos.IsentoTaxaServico, 
              Produtos.CodigoCozinha,   
              Produtos.ItemPorKg,   
              Pedidos.Numero,
              Pedidos.CodigoPedidoOrigem,
              COALESCE(Cozinhas.Descricao, '') AS DescricaoCozinha,
              COALESCE(Cozinhas.Impressora, '') AS Impressora,
              COALESCE(Cozinhas.Porta, '') AS Porta,
              COALESCE(Cozinhas.IP, '') AS IP,              
              COALESCE(Colaboradores.Nome, COALESCE(ColaboradorPedido.Nome, 'Nao Informado')) AS NomeColaborador,              
              CAST(ItensPedido.Quantidade * ItensPedido.ValorUnitario AS DECIMAL(16, 4)) Subtotal,
			  Pagamentos.Observacao
         FROM ItensPedido
              INNER JOIN Pedidos ON Pedidos.Codigo = ItensPedido.CodigoPedido
              INNER JOIN Produtos ON ItensPedido.CodigoProduto = Produtos.Codigo
              LEFT JOIN Cozinhas ON Cozinhas.Codigo = Produtos.CodigoCozinha              
              LEFT JOIN Contatos Colaboradores ON Colaboradores.Codigo = ItensPedido.CodigoColaborador    
              
              LEFT JOIN Contatos ColaboradorPedido ON ColaboradorPedido.Codigo = Pedidos.CodigoColaborador   
              LEFT JOIN Pagamentos ON Pagamentos.Codigo = ItensPedido.CodigoPagamento
        WHERE 
              ITENSPEDIDO.DATADELETE IS NULL
                        
        ORDER BY ItensPedido.Codigo;
;



