CREATE OR ALTER VIEW VWNFCE(
    CODIGO,
    PEDIDO,
    CODIGONUMERICOALEATORIO,
    SERIE,
    NUMERO,
    CHAVEACESSO,
    SITUACAONFCE,
    JUSTIFICATIVACANCELAMENTO,
    PROTOCOLOCANCELAMENTO,
    DATAHORAEMISSAO,
    DATAFECHAMENTO,
    TIPODOCUMENTODESTINATARIO,
    NUMERODOCUMENTODESTINATARIO,
    SITUACAODESCRICAO,
    TIPODOCUMENTODESTINATARIONOME,
    PENDSOLICPGTOFISCAL)
AS
select
		nf.Codigo, nf.Pedido,
		nf.CodigoNumericoAleatorio, nf.Serie,
		nf.Numero, nf.ChaveAcesso,
		nf.SituacaoNFCe, nf.JustificativaCancelamento,
		nf.ProtocoloCancelamento,
		nf.DataHoraEmissao,
		p.DataFechamento, p.TipoDocumentoDestinatario,
		p.NumeroDocumentoDestinatario, s.Descricao as SituacaoDescricao,
		dd.Nome as TipoDocumentoDestinatarioNome,
		(
            select
              count(*)
            from SOLICPAGAMENTOFISCAL spf 
            inner join PAGAMENTOS pg on pg.CODSOLICPAGAMENTOFISCAL = spf.CODIGO
            where
                spf.CODPEDIDO = p.Codigo and
                spf.IDRESPOSTAFISCAL is null and
                pg.DATADELETE is null		  
		) as PendSolicPgtoFiscal
	from NFCe nf
	inner join Pedidos p on p.Codigo = nf.Pedido
	left join SituacaoNFCe s on s.Codigo = nf.SituacaoNFCe
	left join TipoDocumentoDestinatario dd on dd.Codigo = p.TipoDocumentoDestinatario;
;



