CREATE OR ALTER VIEW VWPAGAMENTOS(
    CODIGO,
    CODIGOPEDIDO,
    CODIGOFORMAPAGAMENTO,
    VALOR,
    DATAPAGAMENTO,
    CODIGOCONTATO,
    CODIGOCAIXA,
