/* View: VWPEDIDOS */
CREATE OR ALTER VIEW VWPEDIDOS(
    CODIGO,
    CODIGOCOLABORADOR,
    DATAABERTURA,
    DATAFECHAMENTO,
    NUMERO,
    NOME,
    SENHA,
    SUBTOTALPAGO,
    TOTALSERVICO,
    VALORTOTALITENS,
    TOTALDESCONTO,
    CODIGOCONTATOFIADO,
    VALORENTREGA,
    CODIGOCONTATOCLIENTE,
    TAG,
    TIPODOCUMENTODESTINATARIO,
    NUMERODOCUMENTODESTINATARIO,
    NOTAEMITIDA,
    CONTASOLICITADA,
    QUANTIDADEPESSOAS,
    NOMECONTATOCLIENTE,
    NOMECOLABORADOR,
    ATENDENTES,
    TOTAL)
AS
SELECT 
		p.Codigo, p.CodigoColaborador, p.DataAbertura,
        p.DataFechamento, p.Numero, p.Nome, p.Senha,
        p.SubtotalPago, p.TotalServico, p.ValorTotalItens,
        p.TotalDesconto, p.CodigoContatoFiado, p.ValorEntrega,
        p.CodigoContatoCliente, p.Tag, p.TipoDocumentoDestinatario,
		p.NumeroDocumentoDestinatario, p.NotaEmitida, p.ContaSolicitada, p.QuantidadePessoas, 
        ContatoCliente.Nome AS NomeContatoCliente, c1.Nome AS NomeColaborador,
        LIST(distinct coalesce(c2.Nome, coalesce(c1.Nome, 'Nao Informado')), ', ') as Atendentes,        
        COALESCE(SUM(CAST(i.Quantidade * i.ValorUnitario AS DECIMAL(16, 4))), 0) Total              
	FROM Pedidos p        
        LEFT JOIN ItensPedido i ON p.Codigo = i.CodigoPedido AND i.DATADELETE IS NULL       
        LEFT JOIN Contatos c1 ON p.CodigoColaborador = c1.Codigo
        LEFT JOIN Contatos c2 ON i.CodigoColaborador = c2.Codigo        
        LEFT JOIN Contatos ContatoCliente ON p.CodigoContatoCliente = ContatoCliente.Codigo
	WHERE 
		p.TAG NOT IN('M-NV', 'M-C')
		AND p.DATADELETE IS NULL
    GROUP BY 
		Codigo, CodigoColaborador, DataAbertura, DataFechamento,
		Numero, Nome, Senha, SubtotalPago, TotalServico, ValorTotalItens, TotalDesconto,
		CodigoContatoFiado, ValorEntrega, CodigoContatoCliente, Tag,
		TipoDocumentoDestinatario, NumeroDocumentoDestinatario, NotaEmitida,
		ContaSolicitada, QuantidadePessoas, NomeColaborador, NomeContatoCliente;
;



