    PVF,
    PCP,
    PVP,
    PCM,
    PVM,
    PCG,
    PVG,
    PCT,
    PVT,
    DESCRICAOCOZINHA,
    IMPRESSORA,
    PORTA,
    IP,
    TOTAL,
    MARGEMLUCRO)
AS
SELECT 
        p.CODIGO, p.CODIGOETIQUETA,
        p.NOME, p.PRECOVENDA,
        p.ESTOQUEMINIMO, p.ESTOQUEATUAL,
        p.ESTOQUECONTROLADO, p.DESCONTINUADO,
        p.PRECOCUSTO, p.ITEMPORKG,
        p.CODIGOPERSONALIZADO, p.ITEMCOZINHA,
        p.ISENTOTAXASERVICO, p.CODIGOCOZINHA,
        p.UTILIZANFCE,
		Etiquetas.Descricao,
		COALESCE(ProdutosPrecos.CodigoProduto, 0) CodigoProdutoPreco,
		ProdutosPrecos.Observacao AS ObservacaoProdutoPreco,
		ProdutosPrecos.PCF,
		ProdutosPrecos.PVF,
		ProdutosPrecos.PCP,
		ProdutosPrecos.PVP,
		ProdutosPrecos.PCM,
		ProdutosPrecos.PVM,
		ProdutosPrecos.PCG,
		ProdutosPrecos.PVG,
		ProdutosPrecos.PCT,
		ProdutosPrecos.PVT,              
		Cozinhas.Descricao AS DescricaoCozinha,
		Cozinhas.Impressora,
		Cozinhas.Porta,
		Cozinhas.IP,
		CAST(p.EstoqueAtual * p.PrecoVenda AS DECIMAL(16, 4)) Total,              
		p.PrecoVenda - p.PrecoCusto MargemLucro
	FROM Produtos p
	INNER JOIN Etiquetas ON p.CodigoEtiqueta = Etiquetas.Codigo
	LEFT JOIN Cozinhas   ON p.CodigoCozinha  = Cozinhas.Codigo
	LEFT JOIN ProdutosPrecos ON ProdutosPrecos.CodigoProduto = p.Codigo;
;



/* View: VWRESUMOFINANCEIRO */
CREATE OR ALTER VIEW VWRESUMOFINANCEIRO(
    DATA,
    VALORRECEBER,
    VALORPAGAR)
AS
select
    Data,
    sum(ValorReceber) as ValorReceber,
    sum(ValorPagar) as ValorPagar
  from
  (
    select
      cast(pg.DataPagamento as date) as Data,
      pg.Valor as ValorReceber,
      0 as ValorPagar
    from Pagamentos pg left join PEDIDOS on pg.CODIGOPEDIDO = PEDIDOS.CODIGO
    where 
    (PEDIDOS.CODIGO IS NOT NULL AND PEDIDOS.DATADELETE IS NULL) OR (PEDIDOS.CODIGO IS NULL)
    union all
    select
      coalesce(cp.DataPagamento, cp.DataVencimento) as Data,
      0 as ValorReceber,
      coalesce(cp.ValorPago, cp.Valor) as ValorPagar
    from ContasPagar cp
  ) Resumo
  group by
    Data;
;



