CREATE OR ALTER PROCEDURE RECUPERARSERIENFCE (
    VNOMEESTACAO VARCHAR(50))
RETURNS (
    VSERIE SMALLINT,
    VRETORNO SMALLINT,
    VMENSAGEMRETORNO VARCHAR(255))
AS
begin
  -- Inicializar os dados de retorno
  vRetorno = 0;
  vMensagemRetorno = null;

  select
    Serie
  from SeriePorEstacaoNFCe
  where
    NomeEstacao = :vNomeEstacao
  into
    :vSerie;
    
  if (vSerie is null) then
  begin
    vSerie = gen_id(GEN_SERIEPORESTACAONFCE_ID, 1);
  insert into SeriePorEstacaoNFCe
  (Serie, NomeEstacao)
  values
  (:vSerie, :vNomeEstacao);
  end  
end^



SET TERM ; ^

